package co.edu.unac.poo1.alimentos;import co.edu.unac.poo1.procesos.Madurados;public abstract class Arepa implements Madurados {    private Integer tamaho;    private Float precio;    public Arepa() {    }    public Arepa(Integer tamaho, Float precio) {        this.tamaho = tamaho;        this.precio = precio;    }    public Integer getTamaho() {        return tamaho;    }    public void setTamaho(Integer tamaho) {        this.tamaho = tamaho;    }    public Float getPrecio() {        return precio;    }    public void setPrecio(Float precio) {        this.precio = precio;    }}